import React, { useState } from 'react';

export default function TextForm(props) {
    const handelUpClick = ()=>{
     console.log("uppercase was clicked" + text);
     let newText = text.toUpperCase;
     setText(newText)
    }

    const handelOnChange = (event)=>{
        console.log("on Change");
        setText(event.target.value);
       }
    const [text, setText] = useState("Enter text here");

  return (
    <div>
      <div className="mb-1">
        <h3 className="mb-3">{props.headings}</h3>
        <textarea className="form-control" value={text} onChange={handelOnChange} id="myBox" rows="8"></textarea>
      </div>
      <button className="btn btn-primary my-2" onClick={handelUpClick}> Convert to uppercase</button>
    </div>
  );
}
