import React, { useState } from 'react';

export default function TextForm(props) {
//   let revert = "";

     
    const handelUpClick = ()=>{
     console.log("uppercase was clicked" + text);
     let newText = text.toUpperCase();
    //  revert=text
     setText(newText)
    }
    const handelDownClick = ()=>{
        console.log("Lowercase was clicked" + text);
        let newText = text.toLowerCase();
        setText(newText)
       }

    //    const handelCapitalizeClick = ()=>{
    //     console.log("Capitalize was clicked" + text);
    //     let newText = text.toLocaleUpperCase();
    //     setText(newText)
    //    }

    const handelOnChange = (event)=>{
        console.log("on Change");
        setText(event.target.value);
       }
    const [text, setText] = useState("Enter Text here");

  return (

    <>
    <div className="container">
      <div className="mb-1">
        <h3 className="mb-3">{props.headings}</h3>
        <textarea className="form-control" value={text} onChange={handelOnChange} id="myBox" rows="8" placeholder='Enter Text here'></textarea>
      </div>
      <button className="btn btn-primary my-2 " onClick={handelUpClick}> Convert to uppercase</button>
      <button className="btn btn-primary my-2 mx-2" onClick={handelDownClick}> Convert to Lowercase</button>
      {/* <button className="btn btn-primary my-2" onClick={handelCapitalizeClick}> Capitalize</button> */}

    </div>
    <div className="container my-3">
        <h4>Your Text summary</h4>
        <p>{text.split(" ").length} words , {text.length} characters</p>
        <p>{0.008 * text.split(" ").length}  Minutes read time</p>
        <h5>Preview</h5>
        <p>{text}</p>
    </div>
    </>
  );
}
